typeAliases.type:
{
    /**
     * The id to apply to the Modal element.
     */
    id?: string;

    /**
     * The container to append the Modal as child. If not provided will append to a new div element on the document body.
     */
    parent?: string | HTMLElement | DocumentFragment | null;

    /**
     * A function that returns, or an instance of content to render as modal body
     */
    body: RenderFunction | RenderResult;

    /**
     * A function that returns, or an instance of content to render in the modal header
     */
    header?: RenderFunction | RenderResult;

    /**
     * A function that returns, or an instance of content to render in the modal footer
     */
    footer?: RenderFunction | RenderResult;

    /**
     * Header text alignment:
     *  - `left` Align header to the left.
     *  - `center` Align header to the center.
     *  - `right` Align header to the right.
     */
    headerAlign?: 'left' | 'center' | 'right';

    /**
     * If true, will not display the header section of the modal
     */
    noHeader?: boolean;

    /**
     * If true, will not display the footer section of the modal
     */
    noFooter?: boolean;

    /**
     * If true will not apply the modal as fullscreen on mobile viewports.
     */
    noFullscreen?: boolean;
}

{
    /**
     * The id to apply to the Alert element.
     */
    id?: string;

    /**
     * A function that returns, or an instance of content to render as the alert status indicator
     */
    statusIndicator?: RenderFunction | RenderResult;

    /**
     * A function that returns, or an instance of content to render in the alert header
     */
    header?: RenderFunction | RenderResult;

    /**
     * A function that returns, or an instance of content to render as alert body
     */
    body?: RenderFunction | RenderResult;

    /**
     * A function that returns, or an instance of content to render as the alert primary action
     */
    primary?: RenderFunction | RenderResult;

    /**
     * A function that returns, or an instance of content to render as the alert secondary action
     */
    secondary?: RenderFunction | RenderResult;

    /**
     * The alert status (Defaults to 'none').
     */
    status?: 'success' | 'warning' | 'error' | 'info' | 'none';

    /**
     * The alert header message.
     */
    message?: string;

    /**
     * Header content horizontal alignment:
     *  - `left` Align header to the left.
     *  - `center` Align header to the center. (Default)
     *  - `right` Align header to the right.
     */
    headerAlign?: 'left' | 'center' | 'right';

    /**
     * The alert detail message.
     */
    description?: string;

    /**
     * Description content horizontal alignment:
     *  - `left` Align description content to the left.
     *  - `center` Align description content to the center. (Default)
     *  - `right` Align description content to the right.
     */
    descriptionAlign?: 'left' | 'center' | 'right';

    /**
     * The primary action button label (Defaults to 'Ok').
     */
    primaryAction?: string;

    /**
     * The secondary action button label (Defaults to 'Cancel').
     */
    secondaryAction?: string;

    /**
     * If true, will provide a secondary action button.
     */
    enableSecondary?: boolean;

    /**
     * Action button(s) horizontal alignment:
     *  - `left` Align action button(s) to the left.
     *  - `center` Align action button(s) to the center.
     *  - `right` Align action button(s) to the right. (Default)
     *  - `stretch` Align action button(s) stretched to fill the horizontal space.
     */
    actionAlign?: 'left' | 'center' | 'right' | 'stretch';
}

{
    args: T;
    story: ComponentStoryFormat<T>;
    canvasElement: HTMLElement;
}

{
    /**
     * Custom render function for the story. (standard)
     */
    render?: (args: T) => any;

    /**
     * Suitable name for the story. (standard)
     */
    name?: string;

    /**
     * Suitable description for the story. (non-standard)
     */
    description?: string | (() => any);

    /**
     * Dynamic set of arguments for the story to execute. (standard)
     */
    args?: Partial<T>;

    /**
     * Test function for the story (standard)
     */
    play?: PlayFunction<T>;

    /**
     * Obtain original DOM source (non-standard)
     */
    //source?: () => string;

    /**
     * Obtain source per framework (non-standard)
     */
    frameworkSources?: FrameworkSource<T>[];
}

{
    /**
     * Framework that the source relates to.
     */
    framework: FrameworkOption;
    /**
     * Function that generates a source string from provided args.
     */
    load?: (args: T, self: FrameworkSource<T>) => string;

    /**
     * Indicates that code is not valid for translating to CodePen.
     */
    disableCodePen?: boolean;

    /**
     * Optional specific source parts for use when additional source is required.
     */
    sourceParts?: SourceParts<T>;
}

{
    /**
     * Fragment source that will be injected after imports and before content in CodePen js file.
     */
    jsFragment?: string | ((args: T) => string);

    /**
     * Fragment source that will be injected in relevant html part for CodePen.
     */
    htmlFragment?: string | ((args: T) => string);
    /**
     * Fragment source that will be injected at the end of CodePen css file.
     */
    cssFragment?: string | ((args: T) => string);
    /**
     * Will force CodePen generation as fallback HTML framework even if specified as other framework.
     */
    fallbackFramework?: boolean;
}

